import React from 'react';



function Verification  (){
    return (

    <React.Fragment>
        <section className="ver-card">
            <div className="slogo-container"></div>
            <div className="editVer-section">
                <h3>SECTION HEADER:</h3>
                <input type="text" placeholder="Overview" class="ver-text" />
                <h3>SECTION BODY:</h3>
                <input type="text" placeholder="Sephora is a French multinational chain of personal care and beauty stores. Featuring nearly 3,000 brands,
                along with its own private label, Sephora offers beauty products including cosmetics, skincare, body, fragrance, 
                nail color, beauty tools, and haircare. The company was founded in Limoges in 1970 and is currently based in Paris. Sephora is owned by luxury conglomerate
                 LVMH as of 1997. The name comes from the Greek spelling of Zipporah (Greek: Σεπφώρα, Sepphōra), wife of Moses." class="ver-text" />
            </div>
        </section>





    </React.Fragment>
    )
} 

export default Verification ;
